const navLinks=document.querySelectorAll(".nav-item"),menuToggle=document.getElementById("navbarSupportedContent");navLinks.forEach((e=>{e.addEventListener("click",(()=>{menuToggle.classList.remove("show")}))}));const sections=document.querySelectorAll(".section"),observer=new IntersectionObserver((e=>{e.forEach((e=>{e.isIntersecting&&e.target.classList.add("animate")}))}));sections.forEach((e=>{observer.observe(e)}));